--
-- 表的结构 `web_index`
--

CREATE TABLE IF NOT EXISTS `web_index` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `content` text COLLATE utf8_unicode_ci,
  `online_type` tinyint(4) DEFAULT NULL,
  `news_no` tinyint(4) DEFAULT NULL,
  `product_no` tinyint(4) DEFAULT NULL,
  `img_no` tinyint(4) DEFAULT NULL,
  `download_no` tinyint(4) DEFAULT NULL,
  `link_no` tinyint(4) DEFAULT NULL,
  `link_img` tinyint(4) DEFAULT NULL,
  `link_text` tinyint(4) DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=MyISAM  DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci AUTO_INCREMENT=2 ;

--
-- 转存表中的数据 `web_index`
--

INSERT INTO `web_index` (`id`, `content`, `online_type`, `news_no`, `product_no`, `img_no`, `download_no`, `link_no`, `link_img`, `link_text`) VALUES
(1, '<p><img hspace="5" alt="" vspace="5" align="left" style="width: 237px; height: 140px" src="Public/images/about.jpg" /><!--EndFragment--></p>\n<div align="left"><span style="font-size: small"><font face="宋体">&nbsp;&nbsp;&nbsp; 维斯特有限公司是和松下电工（中国）有限公司合作、合资的企业，是一家集住宅建材商品、设计、施工、服务于一体的装饰企业。</font></span></div>\n<div align="left"><span style="font-size: small"><font face="宋体">&nbsp;&nbsp;&nbsp;&nbsp;公司倡导构筑中国新型家居环境，致力于为中国消费者实现家居最大舒适化。凭借Panasonic优质的产品；强大的商品开发技术；一流的施工管理为后盾,秉承精艺良工的基本理念，以优质的服务和人性化的设计为客户提供全方位的品质保证，因此也得到了客户以及社会的肯定，在客户方面65% 的客户群来自于老客户的介绍，而在社会方面,公司近年来的工作也得到了&ldquo;室内装饰、设计放心企业&rdquo;、&ldquo;质量诚信单位&rdquo;&ldquo;室内装饰行业诚信企业&rdquo;的称号。</font></span></div>\n<div align="left"><font size="3" face="宋体">&nbsp;&nbsp;&nbsp;&nbsp;</font></div>', 1, 8, 4, 10, 10, NULL, 8, 6);

-- --------------------------------------------------------

--
-- 表的结构 `web_job`
--

CREATE TABLE IF NOT EXISTS `web_job` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `position` varchar(200) COLLATE utf8_unicode_ci NOT NULL DEFAULT ' ',
  `count` int(11) NOT NULL DEFAULT '0',
  `place` varchar(200) COLLATE utf8_unicode_ci NOT NULL DEFAULT ' ',
  `deal` varchar(200) COLLATE utf8_unicode_ci NOT NULL DEFAULT ' ',
  `addtime` int(11) NOT NULL DEFAULT '0',
  `useful_life` int(11) NOT NULL DEFAULT '0',
  `content` text COLLATE utf8_unicode_ci NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=MyISAM  DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci AUTO_INCREMENT=4 ;

--
-- 转存表中的数据 `web_job`
--

INSERT INTO `web_job` (`id`, `position`, `count`, `place`, `deal`, `addtime`, `useful_life`, `content`) VALUES
(1, '销售工程师', 0, ' 长沙', ' ', 2017, 0, '<p><span class="Apple-style-span" style="widows: 2; text-transform: none; text-indent: 0px; border-collapse: separate; font: 16px Simsun; white-space: normal; orphans: 2; letter-spacing: normal; color: rgb(0,0,0); word-spacing: 0px; -webkit-border-horizontal-spacing: 0px; -webkit-border-vertical-spacing: 0px; -webkit-text-decorations-in-effect: none; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px"><span class="Apple-style-span" style="line-height: 23px; font-size: 14px">\n<p style="padding-bottom: 0px; line-height: 23px; border-right-width: 0px; margin: 15px 0px; padding-left: 0px; outline-width: 0px; padding-right: 0px; font-family: inherit; border-top-width: 0px; border-bottom-width: 0px; font-size: 14px; vertical-align: baseline; border-left-width: 0px; padding-top: 0px"><strong>合作：&ldquo;济南事件&rdquo;促成两大卖场战略联手</strong></p>\n<p style="padding-bottom: 0px; line-height: 23px; border-right-width: 0px; margin: 15px 0px; padding-left: 0px; outline-width: 0px; padding-right: 0px; font-family: inherit; border-top-width: 0px; border-bottom-width: 0px; font-size: 14px; vertical-align: baseline; border-left-width: 0px; padding-top: 0px">　　合作中的竞争，竞争中的合作，被称为&ldquo;竞合&rdquo;。居然之家与红星美凯龙在北京的&ldquo;竞合&rdquo;发展态势，到2009年，随着双方在全国连锁战略的推进，逐渐向全国延展，这在当时来说，是让家居界同仁一致叫好的美事儿。</p>\n<p style="padding-bottom: 0px; line-height: 23px; border-right-width: 0px; margin: 15px 0px; padding-left: 0px; outline-width: 0px; padding-right: 0px; font-family: inherit; border-top-width: 0px; border-bottom-width: 0px; font-size: 14px; vertical-align: baseline; border-left-width: 0px; padding-top: 0px">　　&ldquo;济南事件&rdquo;被称为一次成功的&ldquo;竞合&rdquo;案例，它促成了居然之家与红星美凯龙两个巨头之间达成了首次合作。事件发生在2009年2月10日，居然之家和红星美凯龙同时对外证实，红星美凯龙在济南和泰安的两家加盟店，由居然之家全面接管，居然之家借此机会开出济南北园店和泰安店。此前，居然之家在济南已有一家分店&mdash;&mdash;槐荫店。</p>\n<p style="padding-bottom: 0px; line-height: 23px; border-right-width: 0px; margin: 15px 0px; padding-left: 0px; outline-width: 0px; padding-right: 0px; font-family: inherit; border-top-width: 0px; border-bottom-width: 0px; font-size: 14px; vertical-align: baseline; border-left-width: 0px; padding-top: 0px">　　位于济南历山北路上的红星美凯龙济南店开业于2005年3月份，位于泰安的红星美凯龙世博家居广场开业于2008年6月份，这两家店的物业均属于山东现代美家居商城有限公司。对于两家店终止与红星美凯龙的合作，转而变成居然之家分店，一个最重要的原因是红星美凯龙在济南北园地区，也就是济南加盟店的对面，正在建造一个总建筑面积达16.6万平方米的分店(预计在2010年底开业)。为了不重复建设和浪费资源，红星美凯龙董事长兼CEO车建新专门与居然之家总裁汪林朋接洽，将红星美凯龙的两家加盟店和平易手居然之家，达成了这次被称为&ldquo;完美&rdquo;的合作。</p>\n<p style="padding-bottom: 0px; line-height: 23px; border-right-width: 0px; margin: 15px 0px; padding-left: 0px; outline-width: 0px; padding-right: 0px; font-family: inherit; border-top-width: 0px; border-bottom-width: 0px; font-size: 14px; vertical-align: baseline; border-left-width: 0px; padding-top: 0px">　　在&ldquo;济南事件&rdquo;达成圆满合作之后10天，2009年2月20日，中国家居界最有代表性的两位企业家&mdash;&mdash;张学武与车建新的手紧紧地握到了一起，在北京世纪金源大饭店举行盛大新闻发布会，宣布双方结成战略合作伙伴。双方商定的《居然之家红星美凯龙结成战略合作伙伴协议书》包括四个方面内容：一是共同打造公平、公正的市场竞争环境，双方均欢迎对方进入任何城市进行品牌连锁发展；二是联手抵制行业不正之风，不做任何相互诋毁、相互拆台的行为，对其他同行实施的封锁市场等行业不正之风进行联手抵制；三是保持信息沟通，相互交流开店信息、推荐开店项目；四是一方未经过另一方同意，不得与另一已开分店、加盟店的合作方进行有损于对方利益的接洽与合作。</p>\n<p style="padding-bottom: 0px; line-height: 23px; border-right-width: 0px; margin: 15px 0px; padding-left: 0px; outline-width: 0px; padding-right: 0px; font-family: inherit; border-top-width: 0px; border-bottom-width: 0px; font-size: 14px; vertical-align: baseline; border-left-width: 0px; padding-top: 0px">　　在与居然之家董事长张学武签约之后，车建新异常兴奋地亲口阐述此次合作：&ldquo;红星美凯龙在济南北园店对面新买了一块地正建直营店，为处理好直营店与加盟店的关系，我们主动找到居然之家，正好居然之家想在山东发展，苦于没有合适的位置，双方一拍即合，迅速达成合作。&rdquo;张学武对车建新的说法表示认可，并称&ldquo;以后有很多这样的机会，双方要继续整合资源、优势互补、共同发展，把家居行业做大做强&rdquo;。</p>\n<p style="padding-bottom: 0px; line-height: 23px; border-right-width: 0px; margin: 15px 0px; padding-left: 0px; outline-width: 0px; padding-right: 0px; font-family: inherit; border-top-width: 0px; border-bottom-width: 0px; font-size: 14px; vertical-align: baseline; border-left-width: 0px; padding-top: 0px">　　<strong>争端：居然、红星暗战&ldquo;美罗城&rdquo;</strong></p>\n<p style="padding-bottom: 0px; line-height: 23px; border-right-width: 0px; margin: 15px 0px; padding-left: 0px; outline-width: 0px; padding-right: 0px; font-family: inherit; border-top-width: 0px; border-bottom-width: 0px; font-size: 14px; vertical-align: baseline; border-left-width: 0px; padding-top: 0px">　　&ldquo;居然之家红星美凯龙结成战略合作伙伴协议&rdquo;的签订让两大卖场一度步入了和平共处的&ldquo;蜜月期&rdquo;。然而，这只是表象，2008年底的&ldquo;美罗城&rdquo;之争还是暴露了两大卖场的商战事实。</p>\n<p style="padding-bottom: 0px; line-height: 23px; border-right-width: 0px; margin: 15px 0px; padding-left: 0px; outline-width: 0px; padding-right: 0px; font-family: inherit; border-top-width: 0px; border-bottom-width: 0px; font-size: 14px; vertical-align: baseline; border-left-width: 0px; padding-top: 0px">　　北京美罗城位于东四环大郊亭桥西侧，由新加坡上市企业美罗控股有限公司与中国工艺美术(集团)公司共同投资建立的北京华茂置业有限公司负责运营。整个美罗城项目面积15万平方米，从南至北排列，分别是A座4万平方米、B座2.2万平方米、C座9万平方米。其中A座和B座由美罗城自营，C座则由国美、沃尔玛、百盛等主力店瓜分，2007年9月21日宣布全面开业。而位于美罗城南边的红星美凯龙东四环店，作为红星美凯龙进入京城后的第二家店，已先期于2006年国庆开门营业。</p>\n<p style="padding-bottom: 0px; line-height: 23px; border-right-width: 0px; margin: 15px 0px; padding-left: 0px; outline-width: 0px; padding-right: 0px; font-family: inherit; border-top-width: 0px; border-bottom-width: 0px; font-size: 14px; vertical-align: baseline; border-left-width: 0px; padding-top: 0px">　　由于经营不善与水土不服，开业不到两年，美罗城A座与B座的经营已经陷入窘境。居然之家2009年初开始与美罗城进行接洽，并就主要商务条款达成了一致，居然之家希望拿下美罗城A座和B座后，将其打造成专司经营高端进口家具和家居用品的卖场。然而，正当居然之家与美罗城的谈判进入尾声的时候，红星美凯龙于2009年4月份突然介入，通过每平米提高0.4元租金的方式，拿下了美罗城4万平方米面积的A座，并宣布将其改造成建材馆，其南侧50米的原红星美凯龙东四环店则改造为家具馆。时任红星美凯龙华北区总经理的沈耀俊曾以自豪的口气表示，&ldquo;从决定拿下美罗城，到签署长达10年的合作协议，仅用了一个小时&rdquo;。</p>\n<p style="padding-bottom: 0px; line-height: 23px; border-right-width: 0px; margin: 15px 0px; padding-left: 0px; outline-width: 0px; padding-right: 0px; font-family: inherit; border-top-width: 0px; border-bottom-width: 0px; font-size: 14px; vertical-align: baseline; border-left-width: 0px; padding-top: 0px">　　红星美凯龙的突然行动在居然之家看来是背后捅刀子！居然之家与美罗城谈判的信息是公开的，汪林朋也与车建新打过招呼，双方还曾表示一起合作打造新的家居商圈。但在业内人士看来，居然之家和红星美凯龙两家卖场定位有很多相似的地方，在市场上存在着不可避免的竞争，北京东四环店作为红星美凯龙的北京旗舰店，不可能容忍直接竞争对手在自己家门口开店。</p>\n<p style="padding-bottom: 0px; line-height: 23px; border-right-width: 0px; margin: 15px 0px; padding-left: 0px; outline-width: 0px; padding-right: 0px; font-family: inherit; border-top-width: 0px; border-bottom-width: 0px; font-size: 14px; vertical-align: baseline; border-left-width: 0px; padding-top: 0px">　　但最终这一事件仍以和平收场。红星美凯龙董事长兼CEO车建新表示，对美罗城事件并不知情。最终经过谈判，红星美凯龙与居然之家重归于好。双方还签订了和解协议，协议内容包括：一是无论居然之家同百联谈判成功与否，红星美凯龙都不再租赁和购买上海百联重庆项目；二是居然之家对红星美凯龙在北京美罗城的招商不采取任何限制和抵制措施；三是红星美凯龙承诺在北京开店后不进行任何形式的价格战；四是双方在连锁发展过程中，原则上避开对方已有店面5公里以上，的确不能避开时，应当及时向对方进行通报。</p>\n<p style="padding-bottom: 0px; line-height: 23px; border-right-width: 0px; margin: 15px 0px; padding-left: 0px; outline-width: 0px; padding-right: 0px; font-family: inherit; border-top-width: 0px; border-bottom-width: 0px; font-size: 14px; vertical-align: baseline; border-left-width: 0px; padding-top: 0px">　　&ldquo;美罗城&rdquo;争端差一点让居然之家和红星美凯龙的合作关系破裂，但红星美凯龙和居然之家真的会就此和平发展下去吗？</p>\n<p style="padding-bottom: 0px; line-height: 23px; border-right-width: 0px; margin: 15px 0px; padding-left: 0px; outline-width: 0px; padding-right: 0px; font-family: inherit; border-top-width: 0px; border-bottom-width: 0px; font-size: 14px; vertical-align: baseline; border-left-width: 0px; padding-top: 0px">　　<strong>升级：&ldquo;重庆上海城&rdquo;项目再爆争端</strong></p>\n<p style="padding-bottom: 0px; line-height: 23px; border-right-width: 0px; margin: 15px 0px; padding-left: 0px; outline-width: 0px; padding-right: 0px; font-family: inherit; border-top-width: 0px; border-bottom-width: 0px; font-size: 14px; vertical-align: baseline; border-left-width: 0px; padding-top: 0px">　　&ldquo;美罗城事件&rdquo;刚刚平息不久，双方在重庆再次爆发争端。&ldquo;上海城&rdquo;位于重庆南坪商圈，业主方为上海百联(集团)有限公司，营业面积13万平方米。居然之家和红星美凯龙均看中了此项目，双方在北京就美罗城项目达成和解时，明确约定：红星美凯龙承诺，不再承租上海百联重庆项目，无论居然之家同百联谈判成功与否，红星美凯龙都不再租赁或购买百联的该项目，居然之家承租该项目后用于经营家具；居然之家承诺，不再承租上海百联重庆项目旁的电子城项目(约2万平方米)，由红星美凯龙承租后连同原来承租的面积(共计约7万平方米)用于经营建材。双方约定，&ldquo;共同打造重庆家具建材商圈&rdquo;。</p>\n<p style="padding-bottom: 0px; line-height: 23px; border-right-width: 0px; margin: 15px 0px; padding-left: 0px; outline-width: 0px; padding-right: 0px; font-family: inherit; border-top-width: 0px; border-bottom-width: 0px; font-size: 14px; vertical-align: baseline; border-left-width: 0px; padding-top: 0px">　　随后，业内爆出2009年10月，红星美凯龙通过每平方米提高0.3元租金的方式，从上海百联手中竞得物业方合作权，但本次事件并未升级，双方仍然在保持着克制，但明眼人表示，双方的战略合作关系已经破裂。</p>\n<p style="padding-bottom: 0px; line-height: 23px; border-right-width: 0px; margin: 15px 0px; padding-left: 0px; outline-width: 0px; padding-right: 0px; font-family: inherit; border-top-width: 0px; border-bottom-width: 0px; font-size: 14px; vertical-align: baseline; border-left-width: 0px; padding-top: 0px">　　<strong>举报：北京五大卖场联合状告红星不正当竞争</strong></p>\n<p style="padding-bottom: 0px; line-height: 23px; border-right-width: 0px; margin: 15px 0px; padding-left: 0px; outline-width: 0px; padding-right: 0px; font-family: inherit; border-top-width: 0px; border-bottom-width: 0px; font-size: 14px; vertical-align: baseline; border-left-width: 0px; padding-top: 0px">　　2009年10月，一份由北京市场协会家居分会、北京家具行业协会、居然之家、城外诚、集美、蓝景丽家和爱家等单位敲章的投诉书递到了商务部，指称红星美凯龙的&ldquo;满3000元直送1600元&rdquo;涉嫌赔本倾销，违反《反不正当竞争法》，商务部责成北京市工商局展开调查。</p>\n<p style="padding-bottom: 0px; line-height: 23px; border-right-width: 0px; margin: 15px 0px; padding-left: 0px; outline-width: 0px; padding-right: 0px; font-family: inherit; border-top-width: 0px; border-bottom-width: 0px; font-size: 14px; vertical-align: baseline; border-left-width: 0px; padding-top: 0px">　　据了解，在2009年国庆期间和2010年春，红星美凯龙在北京各店举行了大规模促销，其中最为关注的是&ldquo;满3000元，直送现金1600元&rdquo;的大幅降价促销活动。虽然这个活动是红星美凯龙自己企业的事情，但在居然之家等北京几个主流家居卖场看来，红星美凯龙的做法超出了一般商业竞争的底限。&ldquo;家居建材没有那么大的利润，满3000元送1600元，相当于打了不到5折，红星美凯龙是在搞贴钱倾销，扰乱市场秩序，是典型的不正当竞争行为。&rdquo;</p>\n<p style="padding-bottom: 0px; line-height: 23px; border-right-width: 0px; margin: 15px 0px; padding-left: 0px; outline-width: 0px; padding-right: 0px; font-family: inherit; border-top-width: 0px; border-bottom-width: 0px; font-size: 14px; vertical-align: baseline; border-left-width: 0px; padding-top: 0px">　　对于北京市工商局的调查结果，红星美凯龙与居然之家双方说辞不一。据红星美凯龙某位高管的说法是大家都被罚款了，居然之家被罚了400万，是最多的，红星美凯龙只被罚了200万。但居然之家总裁汪林朋表示，这纯属瞎说，居然之家没有被罚，参与不正当竞争的红星美凯龙最终被处罚了数百万元。</p>\n<p style="padding-bottom: 0px; line-height: 23px; border-right-width: 0px; margin: 15px 0px; padding-left: 0px; outline-width: 0px; padding-right: 0px; font-family: inherit; border-top-width: 0px; border-bottom-width: 0px; font-size: 14px; vertical-align: baseline; border-left-width: 0px; padding-top: 0px">　　<strong>揭秘：家居流通巨头间商战愈演愈烈？</strong></p>\n<p style="padding-bottom: 0px; line-height: 23px; border-right-width: 0px; margin: 15px 0px; padding-left: 0px; outline-width: 0px; padding-right: 0px; font-family: inherit; border-top-width: 0px; border-bottom-width: 0px; font-size: 14px; vertical-align: baseline; border-left-width: 0px; padding-top: 0px">　　随着北五环事件的彻底大爆发，居然之家与红星美凯龙的的合作关系已经彻底破裂。有消息称，与居然之家不再是合作关系，也不是竞争关系，而是敌对关系。</p>\n<p style="padding-bottom: 0px; line-height: 23px; border-right-width: 0px; margin: 15px 0px; padding-left: 0px; outline-width: 0px; padding-right: 0px; font-family: inherit; border-top-width: 0px; border-bottom-width: 0px; font-size: 14px; vertical-align: baseline; border-left-width: 0px; padding-top: 0px">　　据小道消息，红星美凯龙计划在北京至少开出10个店，除了目前已有的三家店和正在准备开业的北五环店、秘密签下的北三环店以外，还在东五环、西三环、南五环等处圈地，准备开设新店。红星的扩张之举让北京的几大卖场着实不安。</p>\n<p style="padding-bottom: 0px; line-height: 23px; border-right-width: 0px; margin: 15px 0px; padding-left: 0px; outline-width: 0px; padding-right: 0px; font-family: inherit; border-top-width: 0px; border-bottom-width: 0px; font-size: 14px; vertical-align: baseline; border-left-width: 0px; padding-top: 0px">　　在此背景下，北京主流家居卖场也都站到了居然之家一边，集美家居、蓝景丽家、城外诚、爱家家居均表示，凡是自己的合作伙伴，都不准进入红星美凯龙北五环店，否则合同到期后有可能面临租金上涨，甚至不予续约。对于这样做的理由，蓝景丽家总经理尹勃的话颇有代表性：&ldquo;这不是居然之家与红星美凯龙两个企业之间的战争，而是北京主流家居卖场与红星美凯龙之间的生存之战，我们不能坐以待毙，必须与居然之家一道，坚决地进行反击。&rdquo;</p>\n<p style="padding-bottom: 0px; line-height: 23px; border-right-width: 0px; margin: 15px 0px; padding-left: 0px; outline-width: 0px; padding-right: 0px; font-family: inherit; border-top-width: 0px; border-bottom-width: 0px; font-size: 14px; vertical-align: baseline; border-left-width: 0px; padding-top: 0px">　　据了解，红星美凯龙目前在全国有近70家分店，居然之家有近40家店，从店面数量来讲，红星美凯龙稍胜一筹。但在管理和服务上看，居然之家又有自己的一套，行业内就有&ldquo;红星的任何十个店加起来都不如居然之家的一个北四环店&rdquo;的传言。对于在全国市场上最有规模和分量的两大家居卖场的商战，业内人士认为，两家企业都处于高速扩张的成长期，两大美凯龙之间的生存之战，我们不能坐以待毙，必须与居然之家一道，商战的大戏才刚刚拉开序幕，日后的摩擦，&ldquo;明战&rdquo;或&ldquo;暗站&rdquo;将会不时发生。<span class="Apple-converted-space">&nbsp;</span><span style="padding-bottom: 0px; border-right-width: 0px; margin: 0px; padding-left: 0px; outline-width: 0px; padding-right: 0px; font-family: KaiTi_GB2312, KaiTi; border-top-width: 0px; border-bottom-width: 0px; font-size: 14px; vertical-align: baseline; border-left-width: 0px; padding-top: 0px">（文/ 新浪家居 张永志）</span></p>\n</span></span></p>'),
(2, '销售经理', 5, ' 上海', ' ', 2017, 10, ''),
(3, 'php程序员', 3, ' 上海', ' ', 0, 0, '');

